/**
 * Represents the series options for the Chart events.
 */
export interface EventSeriesOptions {
    /**
     * Specifies the index of the series.
     */
    index: number;
    /**
     * Specifies the name of the series.
     */
    name: string;
    /**
     * Specifies the visibility of the series.
     */
    visible: boolean;
}
