/**
 * Represents the context for the axis note `content` function.
 */
export interface AxisNoteContentArgs {
    /**
     * Specifies the original data item for the note.
     */
    dataItem: any;
    /**
     * Specifies the text of the note.
     */
    text: string;
    /**
     * Specifies the value of the note.
     */
    value: string;
}
