/**
 * Specifies the `baseUnit` type of a X or Y axis.
 *
 * The possible values are:
 * - `"milliseconds"`&mdash;Sets the base unit to milliseconds.
 * - `"seconds"`&mdash;Sets the base unit to seconds.
 * - `"minutes"`&mdash;Sets the base unit to minutes.
 * - `"hours"`&mdash;Sets the base unit to hours.
 * - `"days"`&mdash;Sets the base unit to days.
 * - `"weeks"`&mdash;Sets the base unit to weeks.
 * - `"months"`&mdash;Sets the base unit to months.
 * - `"years"`&mdash;Sets the base unit to years.
 *
 * @example
 * ```ts
 * import { Component } from '@angular/core';
 * import { BaseUnit } from '@progress/kendo-angular-charts';
 *
 * _@Component({
 *   selector: 'my-app',
 *   template: `
 *     <kendo-chart>
 *       <kendo-chart-x-axis>
 *         <kendo-chart-x-axis-item [baseUnit]="baseUnit">
 *         </kendo-chart-x-axis-item>
 *       </kendo-chart-x-axis>
 *       <kendo-chart-series>
 *         <kendo-chart-series-item type="scatter" [data]="data">
 *         </kendo-chart-series-item>
 *       </kendo-chart-series>
 *     </kendo-chart>
 *   `
 * })
 * class AppComponent {
 *   public baseUnit: BaseUnit = "months";
 *   public data: any[] = [[new Date(2000, 0, 1), 1], [new Date(2001, 0, 1), 1]];
 * }
 *
 * ```
 */
export type BaseUnit = 'milliseconds' | 'seconds' | 'minutes' | 'hours' | 'days' | 'weeks' | 'months' | 'years';
