import { BaseUnit } from './base-unit';
/**
 * Specifies the `baseUnit` type of the category axis.
 *
 * The possible values are:
 * - [`BaseUnit`](slug:api_charts_baseunit)&mdash;Sets the base time interval for the axis labels.
 * - `"auto"`&mdash;Automatically determines the base unit by the minimum difference between subsequent categories.
 * - `"fit"`&mdash;Adjusts the [`categoryAxis.baseUnitStep`](slug:api_charts_categoryaxis#toc-baseunitstep) and base unit so that the number of categories does not exceed [`categoryAxis.maxDateGroups`](slug:api_charts_categoryaxis#toc-maxdategroups), and uses the [`series.aggregate`](slug:api_charts_series#toc-aggregate) function to aggregate data based on the selected base unit.
 *
 * @example
 * ```ts
 * import { Component } from '@angular/core';
 * import { CategoryBaseUnit } from '@progress/kendo-angular-charts';
 *
 * _@Component({
 *   selector: 'my-app',
 *   template: `
 *     <kendo-chart>
 *       <kendo-chart-category-axis>
 *         <kendo-chart-category-axis-item [baseUnit]="baseUnit" >
 *         </kendo-chart-category-axis-item>
 *       </kendo-chart-category-axis>
 *       <kendo-chart-series>
 *         <kendo-chart-series-item categoryField="category" [data]="data">
 *         </kendo-chart-series-item>
 *       </kendo-chart-series>
 *     </kendo-chart>
 *   `
 * })
 * class AppComponent {
 *   public baseUnit: CategoryBaseUnit = "months";
 *   public data: any[] = [{ category: new Date(2000, 0, 1), value: 1 }, { category: new Date(2001, 0, 1), value: 1}];
 * }
 *
 * ```
 */
export type CategoryBaseUnit = BaseUnit | 'auto' | 'fit';
