import { DashType } from './dash-type';
/**
 * Specifies the appearance settings for the error bar lines.
 */
export interface ErrorBarLine {
    /**
     * Specifies the dash type of the line.
     */
    dashType?: DashType;
    /**
     * Specifies the width of the line in pixels.
     */
    width?: number;
}
