import { drawing } from '@progress/kendo-drawing';
import { AxisNoteVisualArgs, NoteLine, NotePosition } from '../../common/property-types';
import { ValueAxisNotesIcon } from './notes.icon.interface';
import { ValueAxisNotesLabel } from './notes.label.interface';
/**
 * Represents the configuration options of the value axis notes.
 */
export interface ValueAxisNotes {
    /**
     * Specifies the items of the notes.
     */
    data?: any[];
    /**
     * Specifies the line of the notes.
     */
    line?: NoteLine;
    /**
     * Specifies the position of the value axis note.
     */
    position?: NotePosition;
    /**
     * Specifies a function for creating custom visuals for the notes.
     */
    visual?: (e: AxisNoteVisualArgs) => drawing.Element;
    /**
     * Specifies the icon of the notes.
     */
    icon?: ValueAxisNotesIcon;
    /**
     * Specifies the label of the notes.
     */
    label?: ValueAxisNotesLabel;
}
