import { AxisNoteContentArgs, Border, NoteLabelPosition } from '../../common/property-types';
/**
 * Represents the value axis notes label options.
 */
export interface ValueAxisNotesLabel {
    /**
     * Sets the background color of the label.
     * Accepts a valid CSS color string, including HEX and RGB.
     */
    background?: string;
    /**
     * Configures the border of the label.
     */
    border?: Border;
    /**
     * Sets the text color of the label.
     * Accepts a valid CSS color string, including HEX and RGB.
     */
    color?: string;
    /**
     * Sets the function which returns the label content.
     * You can split the text into multiple lines by using line feed characters (`"\n"`).
     */
    content?: (e: AxisNoteContentArgs) => string;
    /**
     * Sets the font style of the label.
     */
    font?: string;
    /**
     * Sets the format used to display the notes label.
     * Uses the `IntlService` [`format`]({% slug api_intl_intlservice %}#toc-format) method and contains one placeholder (`"{0}"`) which represents the axis value.
     */
    format?: string;
    /**
     * Sets the position of the labels.
     */
    position?: NoteLabelPosition;
    /**
     * Sets the rotation angle of the label.
     * By default, the label is not rotated.
     */
    rotation?: number;
    /**
     * Determines if the Chart displays the value axis notes label.
     * By default, the value axis notes label is visible.
     */
    visible?: boolean;
}
