/**
 * Represents the navigator hint content function arguments.
 */
export interface NavigatorHintContentArgs {
    /**
     * Specifies the lower boundary of the selected range.
     */
    from?: Date;
    /**
     * Specifies the upper boundary of the selected range.
     */
    to?: Date;
}
