/**
 * @hidden
 */
export const guid = () => {
    let id = "";
    for (let i = 0; i < 32; i++) {
        const random = Math.random() * 16 | 0; // eslint-disable-line no-bitwise
        if (i === 8 || i === 12 || i === 16 || i === 20) {
            id += "-";
        }
        let charValue;
        if (i === 12) {
            charValue = 4;
        }
        else if (i === 16) {
            // eslint-disable-next-line no-bitwise
            charValue = random & 3 | 8;
        }
        else {
            charValue = random;
        }
        id += charValue.toString(16);
    }
    return id;
};
