/**
 * Represents the available Dialog animation types.
 *
 * Use the `DialogAnimationType` type to specify the animation for Dialog transitions.
 *
 */
export type DialogAnimationType = 'translate' | 'slide' | 'fade' | 'expand' | 'zoom';
